   561→
   562→            // Extract blockTypeGroup from blockTypeId (e.g., "custom" from "custom:stone" or "w" from "w/123")
   563→            String blockTypeGroup = extractGroupFromBlockId(blockTypeId);
   564→
   565→            // Save BlockType
   566→            WBlockType saved = blockTypeService.save(wid, blockTypeId, blockType);
