   240→    @ok="handleInputOk"
   241→    @cancel="handleInputCancel"
   242→  />
   243→</template>
   244→
   245→<script setup lang="ts">
   246→import { ref, computed, onMounted } from 'vue';
   247→import { Dialog, DialogPanel, DialogTitle, TransitionRoot, TransitionChild } from '@headlessui/vue';
   248→import type { BlockType, BlockModifier } from '@nimbus/shared';
   249→import { useBlockTypes } from '@/composables/useBlockTypes';
   250→import { apiService } from '@/services/ApiService';
   251→import JsonEditorDialog from '@components/JsonEditorDialog.vue';
   252→import InputDialog from '@components/InputDialog.vue';
   253→
   254→interface Props {
   255→  blockType: BlockType | null;
   256→  worldId: string;
   257→}
   258→
   259→const props = defineProps<Props>();
   260→
   261→const emit = defineEmits<{
   262→  (e: 'close'): void;
   263→  (e: 'saved'): void;
   264→  (e: 'edit-modifier', data: { blockType: BlockType; status: number; modifier: BlockModifier }): void;
   265→}>();
   266→
   267→const { createBlockType, updateBlockType } = useBlockTypes(props.worldId);
   268→
   269→const isCreate = computed(() => !props.blockType);
   270→const saving = ref(false);
   271→const showJsonEditor = ref(false);
   272→
   273→// Duplicate dialog state
   274→const showDuplicateDialog = ref(false);
   275→const newBlockTypeId = ref('');
   276→const duplicating = ref(false);
   277→const duplicateError = ref<string | null>(null);
   278→
   279→// Input dialog state
   280→const showInputDialog = ref(false);
   281→const inputDialogTitle = ref('');
   282→const inputDialogMessage = ref('');
   283→const inputDialogDefaultValue = ref('');
   284→const inputDialogCallback = ref<((value: string | null) => void) | null>(null);
   285→
   286→// Helper to show input dialog (replaces prompt)
   287→const showInput = (title: string, message: string, defaultValue: string): Promise<string | null> => {
   288→  return new Promise((resolve) => {
   289→    inputDialogTitle.value = title;
   290→    inputDialogMessage.value = message;
   291→    inputDialogDefaultValue.value = defaultValue;
   292→    inputDialogCallback.value = resolve;
   293→    showInputDialog.value = true;
   294→  });
   295→};
   296→
   297→const handleInputOk = (value: string) => {
   298→  if (inputDialogCallback.value) {
   299→    inputDialogCallback.value(value);
   300→    inputDialogCallback.value = null;
   301→  }
   302→};
   303→
   304→const handleInputCancel = () => {
   305→  if (inputDialogCallback.value) {
   306→    inputDialogCallback.value(null);
   307→    inputDialogCallback.value = null;
   308→  }
   309→};
   310→
   311→// Form data
   312→const formData = ref<Partial<BlockType>>({
   313→  id: '',
   314→  description: '',
   315→  initialStatus: 0,
   316→  modifiers: {},
   317→});
   318→
   319→// Expose method to update modifier from parent
   320→const updateModifier = (status: number, modifier: BlockModifier) => {
   321→  if (formData.value.modifiers) {
   322→    formData.value.modifiers[status] = modifier;
   323→  }
   324→};
   325→
   326→// Expose for parent access
   327→defineExpose({
   328→  updateModifier,
   329→  formData
   330→});
   331→
   332→// Initialize form
   333→const initializeForm = async () => {
   334→  if (props.blockType) {
   335→    formData.value = JSON.parse(JSON.stringify(props.blockType));
   336→  } else {
   337→    formData.value = {
   338→      id: '', // Must be provided by user
   339→      description: '',
   340→      initialStatus: 0,
   341→      modifiers: {
   342→        0: { visibility: { shape: 1, textures: {} } }, // Default status with CUBE shape
   343→      },
   344→    };
   345→  }
   346→};
   347→
   348→onMounted(() => {
   349→  initializeForm();
   350→});
   351→
   352→// Status list
   353→const statusList = computed(() => {
   354→  return Object.keys(formData.value.modifiers || {}).map(Number).sort((a, b) => a - b);
   355→});
   356→
   357→// Get modifier summary
   358→const getModifierSummary = (status: number): string => {
   359→  const modifier = formData.value.modifiers?.[status];
   360→  if (!modifier) return 'Empty';
   361→
   362→  const parts: string[] = [];
   363→  if (modifier.visibility) parts.push('visibility');
   364→  if (modifier.physics) parts.push('physics');
   365→  if (modifier.wind) parts.push('wind');
   366→  if (modifier.effects) parts.push('effects');
   367→  if (modifier.illumination) parts.push('illumination');
   368→  if (modifier.audio) parts.push('audio');
   369→
   370→  return parts.length > 0 ? parts.join(', ') : 'Empty';
   371→};
   372→
   373→// Add status
   374→const addStatus = async () => {
   375→  const existingStatuses = statusList.value;
   376→  const nextStatus = existingStatuses.length > 0 ? Math.max(...existingStatuses) + 1 : 1;
   377→
   378→  const statusId = await showInput(
   379→    'Add Status',
   380→    `Enter status ID (default: ${nextStatus}):`,
   381→    nextStatus.toString()
   382→  );
   383→
   384→  if (statusId === null) return; // Cancelled
   385→
   386→  const newStatusId = parseInt(statusId, 10);
   387→
   388→  if (isNaN(newStatusId)) {
   389→    return;
   390→  }
   391→
   392→  if (formData.value.modifiers && formData.value.modifiers[newStatusId]) {
   393→    return;
   394→  }
   395→
   396→  if (!formData.value.modifiers) {
   397→    formData.value.modifiers = {};
   398→  }
   399→
   400→  formData.value.modifiers[newStatusId] = {
   401→    visibility: { shape: 1, textures: {} }
   402→  };
   403→};
   404→
   405→// Change status ID
   406→const changeStatusId = async (oldStatus: number) => {
   407→  if (oldStatus === 0) {
   408→    return;
   409→  }
   410→
   411→  const newStatusIdStr = await showInput(
   412→    'Change Status ID',
   413→    `Change status ID from ${oldStatus} to:`,
   414→    oldStatus.toString()
   415→  );
   416→
   417→  if (newStatusIdStr === null) return; // Cancelled
   418→
   419→  const newStatusId = parseInt(newStatusIdStr, 10);
   420→
   421→  if (isNaN(newStatusId)) {
   422→    return;
   423→  }
   424→
   425→  if (newStatusId === oldStatus) return; // No change
   426→
   427→  if (formData.value.modifiers && formData.value.modifiers[newStatusId]) {
   428→    return;
   429→  }
   430→
   431→  if (!formData.value.modifiers || !formData.value.modifiers[oldStatus]) {
   432→    return;
   433→  }
   434→
   435→  // Copy modifier to new status ID
   436→  formData.value.modifiers[newStatusId] = formData.value.modifiers[oldStatus];
   437→
   438→  // Delete old status
   439→  delete formData.value.modifiers[oldStatus];
   440→};
   441→
   442→// Remove status
   443→const removeStatus = (status: number) => {
   444→  if (status === 0) {
   445→    alert('Cannot remove status 0 (default status)');
   446→    return;
   447→  }
   448→
   449→  if (formData.value.modifiers && formData.value.modifiers[status]) {
   450→    delete formData.value.modifiers[status];
   451→  }
   452→};
   453→
   454→// Edit modifier - emit event to parent
   455→const editModifier = (status: number) => {
   456→  if (!formData.value.modifiers) return;
   457→
   458→  emit('edit-modifier', {
   459→    blockType: formData.value as BlockType,
   460→    status,
   461→    modifier: formData.value.modifiers[status]
   462→  });
   463→};
   464→
   465→// Handle save
   466→const handleSave = async () => {
   467→  if (isCreate.value && !formData.value.id) {
   468→    alert('ID is required');
   469→    return;
   470→  }
   471→
   472→  if (!formData.value.modifiers || Object.keys(formData.value.modifiers).length === 0) {
   473→    alert('At least one modifier (status 0) is required');
   474→    return;
   475→  }
   476→
   477→  saving.value = true;
   478→
   479→  try {
   480→    if (isCreate.value) {
   481→      await createBlockType(formData.value);
   482→    } else {
   483→      await updateBlockType(formData.value.id!, formData.value);
   484→    }
   485→
   486→    emit('saved');
   487→  } catch (err: any) {
   488→    // Extract error message from server response
   489→    let errorMessage = 'Failed to save block type';
   490→    if (err?.response?.data?.error) {
   491→      errorMessage = err.response.data.error;
   492→    } else if (err?.message) {
   493→      errorMessage = err.message;
   494→    }
   495→    alert(errorMessage);
   496→  } finally {
   497→    saving.value = false;
   498→  }
   499→};
   500→
   501→// Handle JSON apply from JSON editor
   502→const handleJsonApply = (jsonData: any) => {
   503→  formData.value = jsonData;
   504→};
   505→
   506→// Duplicate BlockType functionality
   507→const openDuplicateDialog = () => {
   508→  newBlockTypeId.value = '';
   509→  duplicateError.value = null;
   510→  showDuplicateDialog.value = true;
   511→};
   512→
   513→const closeDuplicateDialog = () => {
   514→  showDuplicateDialog.value = false;
   515→  newBlockTypeId.value = '';
   516→  duplicateError.value = null;
   517→};
   518→
   519→const handleDuplicate = async () => {
   520→  if (!newBlockTypeId.value || duplicating.value || !props.blockType?.id) {
   521→    return;
   522→  }
   523→
   524→  duplicating.value = true;
   525→  duplicateError.value = null;
   526→
   527→  try {
   528→    const apiUrl = apiService.getBaseUrl();
   529→    const sourceBlockId = props.blockType.id;
   530→    const url = `${apiUrl}/control/worlds/${props.worldId}/blocktypes/duplicate/${encodeURIComponent(sourceBlockId)}`;
   531→
   532→    const response = await fetch(url, {
   533→      method: 'POST',
   534→      headers: {
   535→        'Content-Type': 'application/json',
   536→      },
   537→      body: JSON.stringify({
   538→        newBlockId: newBlockTypeId.value,
   539→      }),
   540→      credentials: 'include'
   541→    });
   542→
   543→    if (!response.ok) {
   544→      const errorData = await response.json().catch(() => ({ error: response.statusText }));
   545→      duplicateError.value = errorData.error || `Failed to duplicate BlockType: ${response.statusText}`;
   546→      return;
   547→    }
   548→
   549→    const result = await response.json();
   550→
   551→    // Close dialog
   552→    closeDuplicateDialog();
   553→
   554→    // Show success message
   555→    alert(`BlockType duplicated successfully!\n\nNew ID: ${result.blockId}\n\nThe page will reload to show the updated list.`);
   556→
   557→    // Emit saved event to refresh the list
   558→    emit('saved');
   559→
   560→    // Close the editor
   561→    emit('close');
   562→  } catch (err) {
   563→    duplicateError.value = err instanceof Error ? err.message : 'Unknown error occurred';
   564→  } finally {
   565→    duplicating.value = false;
   566→  }
   567→};
   568→</script>
