     3→    <div class="fixed inset-0 z-[100] flex items-center justify-center p-4">
     4→      <!-- Backdrop -->
     5→      <div class="absolute inset-0 bg-black bg-opacity-50"></div>
     6→
     7→      <!-- Dialog Content -->
     8→      <div class="relative w-full max-w-md bg-base-100 rounded-lg shadow-2xl p-6">
     9→        <h3 class="text-lg font-bold mb-4">{{ title }}</h3>
    10→
    11→        <p v-if="message" class="text-sm text-base-content/70 mb-4">{{ message }}</p>
    12→
    13→        <input
    14→          v-model="inputValue"
    15→          type="text"
    16→          class="input input-bordered w-full mb-4"
    17→          :placeholder="placeholder"
    18→          @keyup.enter="handleOk"
    19→          @keyup.esc="handleCancel"
    20→          ref="inputEl"
    21→        />
    22→
    23→        <div class="flex justify-end gap-2">
    24→          <button class="btn btn-ghost" @click="handleCancel">
    25→            Cancel
    26→          </button>
    27→          <button class="btn btn-primary" @click="handleOk">
    28→            OK
    29→          </button>
    30→        </div>
    31→      </div>
    32→    </div>
    33→  </Teleport>
    34→</template>
    35→
    36→<script setup lang="ts">
    37→import { ref, watch, nextTick } from 'vue';
    38→
    39→interface Props {
    40→  isOpen: boolean;
    41→  title: string;
    42→  message?: string;
    43→  defaultValue?: string;
    44→  placeholder?: string;
    45→}
    46→
    47→const props = withDefaults(defineProps<Props>(), {
    48→  message: '',
    49→  defaultValue: '',
    50→  placeholder: ''
    51→});
    52→
    53→const emit = defineEmits<{
    54→  (e: 'update:isOpen', value: boolean): void;
    55→  (e: 'ok', value: string): void;
    56→  (e: 'cancel'): void;
    57→}>();
    58→
    59→const inputValue = ref('');
    60→const inputEl = ref<HTMLInputElement | null>(null);
    61→
    62→// Initialize input value when dialog opens
    63→watch(() => props.isOpen, (isOpen) => {
    64→  if (isOpen) {
    65→    inputValue.value = props.defaultValue;
    66→    nextTick(() => {
    67→      inputEl.value?.focus();
    68→      inputEl.value?.select();
    69→    });
    70→  }
    71→});
    72→
    73→const handleOk = () => {
    74→  emit('ok', inputValue.value);
    75→  emit('update:isOpen', false);
    76→};
    77→
    78→const handleCancel = () => {
    79→  emit('cancel');
    80→  emit('update:isOpen', false);
    81→};
    82→</script>
