    44→            if (blockType.isPresent()) {
    45→                return blockType;
    46→            }
    47→            // Fallback to parent world (COW)
    48→            var parentWorld = collection.worldId().withoutBranchAndInstance();
    49→            return repository.findByWorldIdAndBlockId(parentWorld.getId(), collection.path());
    50→        }
    51→
    52→        return repository.findByWorldIdAndBlockId(collection.worldId().getId(), collection.path());
    53→    }
