 */
@Data
@Builder
@NoArgsConstructor
@AllArgsConstructor
public class LayerChunkData {

    /**
     * Chunk X coordinate.
     */
    private int cx;

    /**
     * Chunk Z coordinate.
     */
    private int cz;

    /**
     * Layer blocks in this chunk.
     */
