    17→@Service
    18→@Slf4j
    19→@RequiredArgsConstructor
    20→public class FlatCreateService {
    21→
    22→    private final WWorldService worldService;
    23→    private final WFlatService flatService;
    24→
    25→    /**
    26→     * Create a new WFlat instance with initialized size.
    27→     * Ocean level is loaded from world info.
    28→     *
    29→     * @param worldId World identifier
    30→     * @param layerDataId Layer data identifier
    31→     * @param flatId Flat identifier
    32→     * @param sizeX Width of the flat (1-800)
    33→     * @param sizeZ Height of the flat (1-800)
    34→     * @param mountX Mount X position
    35→     * @param mountZ Mount Z position
    36→     * @return Created and persisted WFlat instance
    37→     * @throws IllegalArgumentException if world not found or parameters invalid
    38→     */
    39→    public WFlat createFlat(String worldId, String layerDataId, String flatId,
    40→                           int sizeX, int sizeZ, int mountX, int mountZ) {
    41→        log.debug("Creating flat: worldId={}, layerDataId={}, flatId={}, size={}x{}, mount=({},{})",
    42→                worldId, layerDataId, flatId, sizeX, sizeZ, mountX, mountZ);
    43→
    44→        // Load world to get ocean level
    45→        Optional<WWorld> worldOpt = worldService.getByWorldId(worldId);
    46→        if (worldOpt.isEmpty()) {
    47→            throw new IllegalArgumentException("World not found: " + worldId);
    48→        }
    49→
    50→        WWorld world = worldOpt.get();
    51→        int oceanLevel = world.getWaterLevel() != null ? world.getWaterLevel() : 0;
    52→        String oceanBlockId = world.getWaterBlockType();
    53→
    54→        log.debug("Loaded world settings: oceanLevel={}, oceanBlockId={}", oceanLevel, oceanBlockId);
    55→
    56→        // Build WFlat instance
    57→        WFlat flat = WFlat.builder()
    58→                .worldId(worldId)
    59→                .layerDataId(layerDataId)
    60→                .flatId(flatId)
    61→                .mountX(mountX)
    62→                .mountZ(mountZ)
    63→                .oceanLevel(oceanLevel)
    64→                .oceanBlockId(oceanBlockId)
    65→                .build();
    66→
    67→        // Initialize with size
    68→        flat.initWithSize(sizeX, sizeZ);
    69→
    70→        // Persist to database
    71→        WFlat saved = flatService.create(flat);
    72→
    73→        log.info("Created flat: id={}, worldId={}, layerDataId={}, flatId={}, size={}x{}",
    74→                saved.getId(), worldId, layerDataId, flatId, sizeX, sizeZ);
    75→
    76→        return saved;
    77→    }
