     1→<template>
     2→  <div class="space-y-4">
     3→    <!-- Check if world is selected -->
     4→    <div v-if="!currentWorldId" class="alert alert-info">
     5→      <svg class="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
     6→        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
     7→      </svg>
     8→      <span>Please select a world to view chunks.</span>
     9→    </div>
    10→
    11→    <!-- Chunk Editor Content (only shown when world is selected) -->
    12→    <template v-else>
    13→      <!-- Header with Search -->
    14→      <div class="flex flex-col sm:flex-row gap-4 items-stretch sm:items-center justify-between">
    15→        <div class="flex-1">
    16→          <SearchInput
    17→            v-model="searchQuery"
    18→            placeholder="Search chunks by key (e.g., 0:0)..."
    19→            @search="handleSearch"
    20→          />
    21→        </div>
    22→      </div>
    23→
    24→      <!-- Loading State -->
    25→      <LoadingSpinner v-if="loading && chunks.length === 0" />
    26→
    27→      <!-- Error State -->
    28→      <ErrorAlert v-else-if="error" :message="error" />
    29→
    30→      <!-- Empty State -->
    31→      <div v-else-if="!loading && chunks.length === 0" class="text-center py-12">
    32→        <p class="text-base-content/70 text-lg">No chunks found</p>
    33→      </div>
    34→
    35→      <!-- Chunk List -->
    36→      <ChunkList
    37→        v-else
    38→        :chunks="chunks"
    39→        :loading="loading"
    40→        @view="handleViewChunk"
    41→        @mark-dirty="handleMarkDirty"
    42→      />
    43→
    44→      <!-- Pagination Controls -->
    45→      <div v-if="!loading && chunks.length > 0" class="flex flex-col sm:flex-row items-center justify-between gap-4 mt-6">
    46→        <div class="text-sm text-base-content/70">
    47→          Showing {{ ((currentPage - 1) * pageSize) + 1 }}-{{ Math.min(currentPage * pageSize, totalCount) }} of {{ totalCount }} chunks
    48→        </div>
    49→        <div class="flex gap-2">
    50→          <button
    51→            class="btn btn-sm"
    52→            :disabled="!hasPreviousPage"
    53→            @click="handlePreviousPage"
    54→          >
    55→            <svg class="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    56→              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 19l-7-7 7-7" />
    57→            </svg>
    58→            Previous
    59→          </button>
    60→          <div class="flex items-center gap-2 px-4">
    61→            <span class="text-sm">Page {{ currentPage }} of {{ totalPages }}</span>
    62→          </div>
    63→          <button
    64→            class="btn btn-sm"
    65→            :disabled="!hasNextPage"
    66→            @click="handleNextPage"
    67→          >
    68→            Next
    69→            <svg class="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    70→              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5l7 7-7 7" />
    71→            </svg>
    72→          </button>
    73→        </div>
    74→      </div>
    75→
    76→      <!-- Chunk Detail Modal -->
    77→      <ChunkDetailModal
    78→        v-if="selectedChunk"
    79→        :world-id="currentWorldId!"
    80→        :chunk-key="selectedChunk"
    81→        @close="closeChunkDetail"
    82→      />
    83→    </template>
    84→  </div>
    85→</template>
    86→
    87→<script setup lang="ts">
    88→import { ref, computed, watch, onMounted } from 'vue';
    89→import { useWorld } from '@/composables/useWorld';
    90→import { useChunks } from '@/composables/useChunks';
    91→import SearchInput from '@components/SearchInput.vue';
    92→import LoadingSpinner from '@components/LoadingSpinner.vue';
    93→import ErrorAlert from '@components/ErrorAlert.vue';
    94→import ChunkList from '../components/ChunkList.vue';
    95→import ChunkDetailModal from '../components/ChunkDetailModal.vue';
    96→
    97→const { currentWorldId, loadWorlds } = useWorld();
    98→
    99→const chunksComposable = computed(() => {
   100→  if (!currentWorldId.value) return null;
   101→  return useChunks(currentWorldId.value);
   102→});
   103→
   104→const chunks = computed(() => chunksComposable.value?.chunks.value || []);
   105→const loading = computed(() => chunksComposable.value?.loading.value || false);
   106→const error = computed(() => chunksComposable.value?.error.value || null);
   107→const searchQuery = ref('');
   108→
   109→// Paging
   110→const totalCount = computed(() => chunksComposable.value?.totalCount.value || 0);
   111→const currentPage = computed(() => chunksComposable.value?.currentPage.value || 1);
   112→const pageSize = computed(() => chunksComposable.value?.pageSize.value || 50);
   113→const totalPages = computed(() => chunksComposable.value?.totalPages.value || 0);
   114→const hasNextPage = computed(() => chunksComposable.value?.hasNextPage.value || false);
   115→const hasPreviousPage = computed(() => chunksComposable.value?.hasPreviousPage.value || false);
   116→
   117→const selectedChunk = ref<string | null>(null);
   118→
   119→// Load chunks when world changes
   120→watch(currentWorldId, () => {
   121→  if (currentWorldId.value) {
   122→    chunksComposable.value?.loadChunks();
   123→  }
   124→}, { immediate: true });
   125→
   126→onMounted(() => {
   127→  // Load worlds with allWithoutInstances filter
   128→  loadWorlds('allWithoutInstances');
   129→});
   130→
   131→/**
   132→ * Handle search
   133→ */
   134→const handleSearch = (query: string) => {
   135→  if (!chunksComposable.value) return;
   136→  chunksComposable.value.searchChunks(query);
   137→};
   138→
   139→/**
   140→ * Handle view chunk
   141→ */
   142→const handleViewChunk = (chunkKey: string) => {
   143→  selectedChunk.value = chunkKey;
   144→};
   145→
   146→/**
   147→ * Handle mark chunk as dirty
   148→ */
   149→const handleMarkDirty = async (chunkKey: string) => {
   150→  if (!currentWorldId.value) return;
   151→
   152→  if (!confirm(`Mark chunk ${chunkKey} as dirty for regeneration?`)) {
   153→    return;
   154→  }
   155→
   156→  try {
   157→    await chunksComposable.value?.markChunkDirty(chunkKey);
   158→    alert(`Chunk ${chunkKey} marked as dirty successfully!`);
   159→  } catch (e: any) {
   160→    alert(`Failed to mark chunk as dirty: ${e.message}`);
   161→  }
   162→};
   163→
   164→/**
   165→ * Close chunk detail
   166→ */
   167→const closeChunkDetail = () => {
   168→  selectedChunk.value = null;
   169→};
   170→
   171→/**
   172→ * Handle next page
   173→ */
   174→const handleNextPage = () => {
   175→  if (!chunksComposable.value) return;
   176→  chunksComposable.value.nextPage();
   177→};
   178→
   179→/**
   180→ * Handle previous page
   181→ */
   182→const handlePreviousPage = () => {
   183→  if (!chunksComposable.value) return;
   184→  chunksComposable.value.previousPage();
   185→};
   186→</script>
