private Vector3 lastPosition;           // Letzte bekannte Position
private Rotation lastRotation;          // Letzte bekannte Rotation
private Vector3 lastVelocity;           // Letzte bekannte Velocity
private ENTITY_POSES lastPose;          // Letztes bekanntes Pose
private Instant lastPositionUpdateAt;   // Timestamp der letzten Position-Update
private boolean positionChanged;        // Flag: Position hat sich geÃ¤ndert

