/**
 * Get entity by ID.
 * URL: GET /api/worlds/{worldId}/entity/{entityId}
 *
 * Searches for:
 * 1. Player entities in active sessions (entityId starts with "@")
 * 2. Persistent NPC entities in database
 */
@GetMapping("/{worldId}/entity/{entityId}")
@Operation(summary = "Get Entity by ID", description = "Returns Entity instance for a specific entity (player or NPC)")
@ApiResponses({
        @ApiResponse(responseCode = "200", description = "Entity found"),
        @ApiResponse(responseCode = "404", description = "Entity not found")
})
public ResponseEntity<Entity> getEntity(
        @PathVariable String worldId,
        @PathVariable String entityId) {

    log.debug("GET /api/worlds/{}/entity/{}", worldId, entityId);

    // Check if this is a player entity (starts with "@")
    if (entityId.startsWith("@")) {
        // Search in active sessions
        for (PlayerSession session : sessionManager.getAllSessions().values()) {
            if (!session.isAuthenticated()) continue;
            if (session.getWorldId() == null || !worldId.equals(session.getWorldId().getId())) continue;

            String sessionEntityId = session.getEntityId();
            if (entityId.equals(sessionEntityId)) {
                // Found player session - build Entity from session data
                Entity entity = buildPlayerEntity(session);
                log.debug("Found player entity in session: {}", entityId);
                return ResponseEntity.ok(entity);
            }
        }
        log.debug("Player entity not found in active sessions: {}", entityId);
    }

    // Not a player entity or not found in sessions - search database
    return service.findByWorldIdAndEntityId(worldId, entityId)
            .map(WEntity::getPublicData)
            .map(ResponseEntity::ok)
            .orElseGet(() -> {
                log.debug("Entity not found: worldId={}, entityId={}", worldId, entityId);
                return ResponseEntity.notFound().build();
            });
}

/**
 * Build Entity from PlayerSession.
 * Creates a minimal Entity representation with current position.
 */
private Entity buildPlayerEntity(PlayerSession session) {
    Entity entity = new Entity();
    entity.setEntityId(session.getEntityId());
    entity.setDisplayName(session.getDisplayName());

    // Build pathway from current position
    if (session.getLastPosition() != null) {
        EntityPathway pathway = EntityPathway.builder()
                .entityId(session.getEntityId())
                .startAt(System.currentTimeMillis())
                .waypoints(buildWaypoints(session))
                .build();

        entity.setPathway(pathway);
    }

    return entity;
}

/**
 * Build waypoints from session state.
 */
private List<Waypoint> buildWaypoints(PlayerSession session) {
    List<Waypoint> waypoints = new ArrayList<>();
    long now = System.currentTimeMillis();

    if (session.getLastPosition() != null) {
        Waypoint waypoint = Waypoint.builder()
                .timestamp(now)
                .target(session.getLastPosition())
                .rotation(session.getLastRotation())
                .pose(session.getLastPose())
                .build();
        waypoints.add(waypoint);
    }

    return waypoints;
}

