                for (JsonNode pathway : filteredPathways) {
                    // Check if pathway affects this chunk
                    if (pathwayAffectsChunk(pathway, cx, cz)) {
                        // Add sessionId to pathway for client-side filtering
                        if (pathway.isObject()) {
                            ObjectNode pathwayWithSession = ((com.fasterxml.jackson.databind.node.ObjectNode) pathway).deepCopy();
