package de.mhus.nimbus.world.player.ws.redis;

import com.fasterxml.jackson.databind.JsonNode;
import com.fasterxml.jackson.databind.ObjectMapper;
import de.mhus.nimbus.generated.types.EntityPathway;
import de.mhus.nimbus.world.player.ws.BroadcastService;
import de.mhus.nimbus.world.player.ws.SessionManager;
import de.mhus.nimbus.world.player.ws.dto.PathwayContainer;
import de.mhus.nimbus.world.shared.redis.WorldRedisMessagingService;
import jakarta.annotation.PostConstruct;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.stereotype.Service;

import java.util.ArrayList;
import java.util.List;

/**
 * Listens for entity pathway updates from all world-player and world-life pods.
 * Channel pattern: world:*:e.p (subscribes to ALL worlds)
 *
 * Architecture: Each world-player pod can have sessions from any world.
 * Therefore, this listener subscribes to all worlds and filters by worldId
 * in the BroadcastService.
 *
 * Message format:
 * {
 *   "containers": [PathwayContainer, ...],
 *   "affectedChunks": [{"cx": 6, "cz": -13}, ...]
 * }
 *
 * PathwayContainer contains:
 * - pathway: EntityPathway
 * - sessionId: String (originating session)
 * - worldId: String
 */
@Service
@RequiredArgsConstructor
@Slf4j
public class PathwayBroadcastListener {

    private final WorldRedisMessagingService redisMessaging;
    private final BroadcastService broadcastService;
    private final SessionManager sessionManager;
    private final ObjectMapper objectMapper;

    @PostConstruct
    public void subscribeToPathwayUpdates() {
        // Subscribe to ALL worlds using pattern: world:*:e.p
        redisMessaging.subscribeToAllWorlds("e.p", this::handlePathwayUpdate);
        log.info("Subscribed to entity pathway updates for all worlds (pattern: world:*:e.p)");
    }

    /**
     * Handle pathway update from Redis (world-life or world-player pods).
     *
     * @param topic Redis topic (format: "world:{worldId}:e.p")
     * @param message JSON message
     */
    private void handlePathwayUpdate(String topic, String message) {
        try {
            // Extract worldId from topic: "world:main:e.p" -> "main"
            String worldId = extractWorldIdFromTopic(topic);
            if (worldId == null) {
                log.warn("Could not extract worldId from topic: {}", topic);
                return;
            }

            JsonNode data = objectMapper.readTree(message);

            JsonNode containersNode = data.get("containers");
            JsonNode affectedChunks = data.get("affectedChunks");

            if (containersNode == null || !containersNode.isArray()) {
                log.warn("Invalid pathway update: missing containers array");
                return;
            }

            if (affectedChunks == null || !affectedChunks.isArray()) {
                log.warn("Invalid pathway update: missing affectedChunks array");
                return;
            }

            // Parse containers
            List<PathwayContainer> containers = new ArrayList<>();
            for (JsonNode containerNode : containersNode) {
                PathwayContainer container = objectMapper.treeToValue(containerNode, PathwayContainer.class);
                containers.add(container);
            }

            // Broadcast pathways per chunk
            for (JsonNode chunkNode : affectedChunks) {
                int cx = chunkNode.has("cx") ? chunkNode.get("cx").asInt() : 0;
                int cz = chunkNode.has("cz") ? chunkNode.get("cz").asInt() : 0;

                // Collect all pathways for this chunk (grouped by originating session)
                // We need to send them grouped because BroadcastService only accepts one originatingSessionId
                Map<String, List<EntityPathway>> pathwaysByOriginSession = new HashMap<>();

                for (PathwayContainer container : containers) {
                    EntityPathway pathway = container.getPathway();

                    // Check if pathway affects this chunk
                    if (!pathwayAffectsChunk(pathway, cx, cz)) {
                        continue;
                    }

                    // Group by originating sessionId
                    String originSessionId = container.getSessionId() != null ? container.getSessionId() : "none";
                    pathwaysByOriginSession
                        .computeIfAbsent(originSessionId, k -> new ArrayList<>())
                        .add(pathway);
                }

                // Broadcast each group separately (different origin sessions)
                for (Map.Entry<String, List<EntityPathway>> entry : pathwaysByOriginSession.entrySet()) {
                    String originSessionId = entry.getKey().equals("none") ? null : entry.getKey();
                    List<EntityPathway> pathways = entry.getValue();

                    // Convert pathways array to JsonNode
                    JsonNode pathwaysArray = objectMapper.valueToTree(pathways);

                    // Broadcast to all sessions in this chunk (excluding originating session)
                    int sentCount = broadcastService.broadcastToWorld(
                            worldId,          // worldId from topic
                            "e.p",            // messageType
                            pathwaysArray,    // data (pathways ARRAY)
                            originSessionId,  // originatingSessionId - will be filtered out!
                            cx,               // chunk X
                            cz                // chunk Z
                    );

                    log.trace("Broadcasted {} pathways to {} sessions for chunk ({}, {}) [origin={}]",
                            pathways.size(), sentCount, cx, cz, originSessionId);
                }
            }

            log.debug("Handled pathway update: {} containers, {} chunks",
                    containers.size(), affectedChunks.size());

        } catch (Exception e) {
            log.error("Failed to handle pathway update from topic {}: {}", topic, message, e);
        }
    }

    /**
     * Check if pathway affects a specific chunk.
     * Pathway affects chunk if any waypoint is in that chunk.
     */
    private boolean pathwayAffectsChunk(EntityPathway pathway, int cx, int cz) {
        if (pathway.getWaypoints() == null) return false;

        for (de.mhus.nimbus.generated.types.Waypoint waypoint : pathway.getWaypoints()) {
            if (waypoint.getTarget() != null) {
                double x = waypoint.getTarget().getX();
                double z = waypoint.getTarget().getZ();
                int waypointCx = (int) Math.floor(x / 16);
                int waypointCz = (int) Math.floor(z / 16);
                if (waypointCx == cx && waypointCz == cz) {
                    return true;
                }
            }
        }
        return false;
    }

    /**
     * Extract worldId from Redis topic.
     * Topic format: "world:{worldId}:e.p"
     *
     * Handles worldIds with special characters (including ':').
     * Extracts everything between "world:" and the last ":e.p".
     *
     * @param topic Redis topic
     * @return worldId or null if invalid format
     */
    private String extractWorldIdFromTopic(String topic) {
        if (topic == null || !topic.startsWith("world:")) {
            return null;
        }
        // Remove "world:" prefix
        String withoutPrefix = topic.substring(6);

        // Find last occurrence of ":e.p" and extract everything before it
        int lastIndex = withoutPrefix.lastIndexOf(":e.p");
        if (lastIndex > 0) {
            return withoutPrefix.substring(0, lastIndex);
        }

        return null;
    }
}

package de.mhus.nimbus.world.player.ws;

import com.fasterxml.jackson.databind.node.ArrayNode;
import com.fasterxml.jackson.databind.node.ObjectNode;
import de.mhus.nimbus.generated.types.EntityPathway;
import de.mhus.nimbus.generated.types.Waypoint;
import de.mhus.nimbus.generated.types.Vector3;
import de.mhus.nimbus.generated.types.Rotation;
import de.mhus.nimbus.generated.types.ENTITY_POSES;
import de.mhus.nimbus.shared.engine.EngineMapper;
import de.mhus.nimbus.world.player.config.PathwayBroadcastProperties;
import de.mhus.nimbus.world.player.session.PlayerSession;
import de.mhus.nimbus.world.player.ws.dto.PathwayContainer;
import de.mhus.nimbus.world.shared.redis.WorldRedisMessagingService;
import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.scheduling.annotation.Scheduled;
import org.springframework.stereotype.Service;

import java.util.*;

/**
 * Service for generating and broadcasting entity pathways to Redis.
 *
 * Runs a scheduled task every 100ms (configurable) that:
 * 1. Iterates all active sessions
 * 2. Generates EntityPathway from position/rotation/velocity state
 * 3. Filters inactive and unchanged sessions
 * 4. Batches pathways by world
 * 5. Publishes to Redis for distribution to all world-player pods
 */
@Service
@RequiredArgsConstructor
@Slf4j
public class PathwayBroadcastService {

    private final SessionManager sessionManager;
    private final WorldRedisMessagingService redisMessaging;
    private final EngineMapper engineMapper;
    private final PathwayBroadcastProperties properties;

    /**
     * Scheduled task: Generate and broadcast entity pathways every 100ms.
     *
     * Spring @Scheduled with fixedRateString from properties.
     */
    @Scheduled(fixedRateString = "${world.player.pathway-broadcast-interval-ms:100}")
    public void broadcastPathways() {
        try {
            // Group pathway containers by worldId
            Map<String, List<PathwayContainer>> containersByWorld = new HashMap<>();

            // Iterate all active sessions
            for (PlayerSession session : sessionManager.getAllSessions().values()) {
                if (!session.isAuthenticated()) continue;

                // Skip if position hasn't been updated recently
                if (session.isUpdateStale(properties.getEntityUpdateTimeoutMs())) {
                    continue;
                }

                // Skip if position hasn't changed
                if (!session.isPositionChanged()) {
                    continue;
                }

                // Generate pathway for this session
                EntityPathway pathway = generatePathway(session);
                if (pathway != null) {
                    String worldId = session.getWorldId().getId();

                    // Create container with session metadata
                    PathwayContainer container = PathwayContainer.builder()
                        .pathway(pathway)
                        .sessionId(session.getSessionId())
                        .worldId(worldId)
                        .build();

                    containersByWorld
                        .computeIfAbsent(worldId, k -> new ArrayList<>())
                        .add(container);
                }
            }

            // Publish pathways to Redis (grouped by world)
            for (Map.Entry<String, List<PathwayContainer>> entry : containersByWorld.entrySet()) {
                publishPathways(entry.getKey(), entry.getValue());
            }

            if (!containersByWorld.isEmpty()) {
                log.trace("Broadcasted {} pathways across {} worlds",
                    containersByWorld.values().stream().mapToInt(List::size).sum(),
                    containersByWorld.size());
            }

        } catch (Exception e) {
            log.error("Error in pathway broadcast task", e);
        }
    }

    /**
     * Generate EntityPathway from PlayerSession state.
     *
     * Creates a predicted pathway with:
     * - Current position as start waypoint
     * - Predicted target position (current + velocity * predictionTime)
     */
    private EntityPathway generatePathway(PlayerSession session) {
        try {
            Vector3 position = session.getLastPosition();
            Rotation rotation = session.getLastRotation();
            Vector3 velocity = session.getLastVelocity();
            ENTITY_POSES pose = session.getLastPose();

            if (position == null) return null;

            long now = System.currentTimeMillis();
            long predictionMs = properties.getPathwayPredictionTimeMs();

            // Calculate predicted target position
            Vector3 targetPosition = position;
            if (velocity != null &&
                (Math.abs(velocity.getX()) > 0.001 ||
                 Math.abs(velocity.getY()) > 0.001 ||
                 Math.abs(velocity.getZ()) > 0.001)) {

                double predictionSec = predictionMs / 1000.0;
                targetPosition = Vector3.builder()
                    .x(position.getX() + velocity.getX() * predictionSec)
                    .y(position.getY() + velocity.getY() * predictionSec)
                    .z(position.getZ() + velocity.getZ() * predictionSec)
                    .build();
            }

            // Create waypoints: start (now) â target (now + 100ms)
            List<Waypoint> waypoints = new ArrayList<>();

            // Start waypoint (current position)
            waypoints.add(Waypoint.builder()
                .timestamp(now)
                .target(position)
                .rotation(rotation != null ? rotation : Rotation.builder().y(0.0).p(0.0).build())
                .pose(pose != null ? pose : ENTITY_POSES.IDLE)
                .build());

            // Target waypoint (predicted position)
            waypoints.add(Waypoint.builder()
                .timestamp(now + predictionMs)
                .target(targetPosition)
                .rotation(rotation != null ? rotation : Rotation.builder().y(0.0).p(0.0).build())
                .pose(pose != null ? pose : ENTITY_POSES.IDLE)
                .build());

            // Build EntityPathway
            return EntityPathway.builder()
                .entityId(session.getEntityId())  // Format: "@userId:characterId"
                .startAt(now)
                .waypoints(waypoints)
                .isLooping(false)
                .queryAt(now)
                .idlePose(pose)
                .physicsEnabled(true)
                .velocity(velocity)
                .grounded(null)  // Later implementation: proper grounded detection
                .build();

        } catch (Exception e) {
            log.error("Failed to generate pathway for session {}",
                session.getSessionId(), e);
            return null;
        }
    }

    /**
     * Publish pathways to Redis for broadcasting to all pods.
     *
     * Message format:
     * {
     *   "containers": [PathwayContainer, ...],
     *   "affectedChunks": [{"cx": 6, "cz": -13}, ...]
     * }
     */
    private void publishPathways(String worldId, List<PathwayContainer> containers) {
        try {
            // Determine affected chunks from pathways
            Set<ChunkCoordinate> affectedChunks = new HashSet<>();

            for (PathwayContainer container : containers) {
                EntityPathway pathway = container.getPathway();
                for (Waypoint wp : pathway.getWaypoints()) {
                    Vector3 pos = wp.getTarget();
                    int cx = (int) Math.floor(pos.getX() / 16);
                    int cz = (int) Math.floor(pos.getZ() / 16);
                    affectedChunks.add(new ChunkCoordinate(cx, cz));
                }
            }

            // Build Redis message
            ObjectNode message = engineMapper.createObjectNode();
            message.set("containers", engineMapper.valueToTree(containers));

            ArrayNode chunksArray = engineMapper.createArrayNode();
            for (ChunkCoordinate chunk : affectedChunks) {
                ObjectNode chunkNode = engineMapper.createObjectNode();
                chunkNode.put("cx", chunk.cx);
                chunkNode.put("cz", chunk.cz);
                chunksArray.add(chunkNode);
            }
            message.set("affectedChunks", chunksArray);

            String json = engineMapper.writeValueAsString(message);
            redisMessaging.publish(worldId, "e.p", json);

            log.debug("Published {} pathway containers to Redis for world {} ({} chunks)",
                containers.size(), worldId, affectedChunks.size());

        } catch (Exception e) {
            log.error("Failed to publish pathways to Redis", e);
        }
    }

    /**
     * Helper class for chunk coordinate deduplication.
     */
    @Data
    @AllArgsConstructor
    private static class ChunkCoordinate {
        int cx, cz;

        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (!(o instanceof ChunkCoordinate)) return false;
            ChunkCoordinate that = (ChunkCoordinate) o;
            return cx == that.cx && cz == that.cz;
        }

        @Override
        public int hashCode() {
            return Objects.hash(cx, cz);
        }
    }
}

