private static final Map<Integer, Set<FlatPosition>> RELATIVE_HEX_CACHE = new ConcurrentHashMap<>();

public Set<FlatPosition> getFlatPositionSet(WWorld worldEntity) {
    int gridSize = worldEntity.getPublicData().getHexGridSize();
    Set<FlatPosition> relativePositions = RELATIVE_HEX_CACHE.computeIfAbsent(
        gridSize, 
        size -> calculateRelativeHexPositions(size)
    );

    // Transform to absolute positions based on hex center
    double[] center = HexMathUtil.hexToCartesian(publicData.getPosition(), gridSize);
    return relativePositions.stream()
        .map(rel -> new FlatPosition(rel.x() + (int)center[0], rel.z() + (int)center[1]))
        .collect(Collectors.toSet());
}
```

