        entities.forEach(e -> {
            if (e.getCreatedAt() == null) {
                e.touchCreate();
            }
            e.touchUpdate();
        });
        List<WItemType> saved = repository.saveAll(entities);
        log.debug("Saved {} WItemType entities", saved.size());
        return saved;
    }

