/**
 * REST Controller for managing WWorld entities.
 * Provides CRUD operations for worlds within regions.
 */
@RestController
@RequestMapping("/control/regions/{regionId}/worlds")
@RequiredArgsConstructor
public class WWorldController extends BaseEditorController {

    private final WWorldService worldService;

    // DTOs
    public record WorldRequest(
            String worldId,
            String name,
            String description,
            WorldInfo publicData,
            Boolean enabled,
            String parent,
            String branch,
            Integer groundLevel,
            Integer waterLevel,
            String groundBlockType,
            String waterBlockType
    ) {}

    public record WorldResponse(
            String id,
            String worldId,
            String regionId,
            String name,
            String description,
            WorldInfo publicData,
            Instant createdAt,
            Instant updatedAt,
            boolean enabled,
            String parent,
            String branch,
            int groundLevel,
            Integer waterLevel,
            String groundBlockType,
            String waterBlockType,
            Set<String> owner,
            Set<String> editor,
            Set<String> player,
            boolean publicFlag
