   271→    const heights = [
   272→      heightMap[x - 1]?.[z - 1] ?? centerHeight, // NW
   273→      heightMap[x]?.[z - 1] ?? centerHeight, // N
   274→      heightMap[x + 1]?.[z - 1] ?? centerHeight, // NE
   275→      heightMap[x - 1]?.[z] ?? centerHeight, // W
   276→      centerHeight, // Center
   277→      heightMap[x + 1]?.[z] ?? centerHeight, // E
   278→      heightMap[x - 1]?.[z + 1] ?? centerHeight, // SW
   279→      heightMap[x]?.[z + 1] ?? centerHeight, // S
   280→      heightMap[x + 1]?.[z + 1] ?? centerHeight, // SE
   281→    ];
   282→
   283→    // Calculate offsets for 8 corners
   284→    // Corner order: bottom 4, then top 4
   285→    // Offsets are in block units (float): -1.0 to 1.0
   286→    const offsets: number[] = new Array(24).fill(0);
   287→
   288→    // Bottom corners (y offset only, based on height difference)
   289→    const yDiff = y - centerHeight;
   290→
   291→    // Bottom corners: pull down if below terrain, push up if above
   292→    // Scale factor controls smoothness (0.3 = moderate smoothing)
   293→    const bottomOffset = Math.max(-1.0, Math.min(1.0, yDiff * 0.3));
   294→
   295→    // 0: left-back-bottom (influenced by W, NW, N)
   296→    offsets[1] = bottomOffset;
   297→
   298→    // 1: right-back-bottom (influenced by E, NE, N)
   299→    offsets[4] = bottomOffset;
   300→
   301→    // 2: right-front-bottom (influenced by E, SE, S)
   302→    offsets[7] = bottomOffset;
   303→
   304→    // 3: left-front-bottom (influenced by W, SW, S)
   305→    offsets[10] = bottomOffset;
   306→
   307→    // Top corners: adjust based on neighboring terrain
   308→    const topYDiff = y + 1 - centerHeight;
   309→    const topOffset = Math.max(-1.0, Math.min(1.0, topYDiff * 0.3));
   310→
   311→    // 4: left-back-top (influenced by W, NW, N)
   312→    const h4 = (heights[3] + heights[0] + heights[1]) / 3;
   313→    offsets[13] = Math.max(
   314→      -1.0,
   315→      Math.min(1.0, (h4 - centerHeight) * 0.24 + topOffset)
   316→    );
   317→
   318→    // 5: right-back-top (influenced by E, NE, N)
   319→    const h5 = (heights[5] + heights[2] + heights[1]) / 3;
   320→    offsets[16] = Math.max(
   321→      -1.0,
   322→      Math.min(1.0, (h5 - centerHeight) * 0.24 + topOffset)
   323→    );
   324→
   325→    // 6: right-front-top (influenced by E, SE, S)
   326→    const h6 = (heights[5] + heights[8] + heights[7]) / 3;
   327→    offsets[19] = Math.max(
   328→      -1.0,
   329→      Math.min(1.0, (h6 - centerHeight) * 0.24 + topOffset)
   330→    );
   331→
   332→    // 7: left-front-top (influenced by W, SW, S)
   333→    const h7 = (heights[3] + heights[6] + heights[7]) / 3;
   334→    offsets[22] = Math.max(
   335→      -1.0,
   336→      Math.min(1.0, (h7 - centerHeight) * 0.24 + topOffset)
   337→    );
   338→
   339→    // Check if any offset is non-zero
   340→    const hasOffset = offsets.some((o) => o !== 0);
   341→    return hasOffset ? offsets : null;
   342→  }
   343→}
