@Component
public class FlatWorldGenerator extends AbstractTerrainGenerator {

    private static final Logger log = LoggerFactory.getLogger(FlatWorldGenerator.class);

    // Parameter keys
    public static final String PARAM_GROUND_LEVEL = "groundLevel";
    public static final String PARAM_LAYER_COUNT = "layerCount";
    public static final String PARAM_GROUND_BLOCK_TYPE_ID = "groundBlockTypeId";

    // Configuration
    private int groundLevel = 64;
    private int layerCount = 2;  // Surface + subsurface
    private String groundBlockTypeId = "w/310";  // Default grass

    @Override
    public String getExecutorName() {
        return "flat-world-generator";
    }

    @Override
    protected void configureGenerator(Map<String, String> genParams) {
        groundLevel = getIntParameter(genParams, PARAM_GROUND_LEVEL, 64);
        layerCount = getIntParameter(genParams, PARAM_LAYER_COUNT, 2);
        groundBlockTypeId = getBlockTypeId(genParams, PARAM_GROUND_BLOCK_TYPE_ID, "w/310");

        log.info("FlatWorldGenerator configured: groundLevel={}, layerCount={}", 
                 groundLevel, layerCount);
    }

    @Override
    protected int getTerrainHeight(int worldX, int worldZ) {
        return groundLevel;  // Always flat
    }

    @Override
    protected List<LayerBlock> generateBlocksAtPosition(FlatPosition pos, WWorld world, GeneratorContext context) {
        List<LayerBlock> blocks = new ArrayList<>();

        // Generate layers from groundLevel downward
        for (int i = 0; i < layerCount; i++) {
            int y = groundLevel - i;

            Block block = Block.builder()
                .blockTypeId(groundBlockTypeId)
                .position(Vector3.builder()
                    .x(pos.x())
                    .y(y)
                    .z(pos.z())
                    .build())
                .rotation(RotationXY.builder().x(0).y(0).build())
                .build();

            LayerBlock layerBlock = LayerBlock.builder()
                .block(block)
                .weight(1)
                .override(true)
                .group(0)
                .build();

            blocks.add(layerBlock);
        }

        return blocks;
    }

    @Override
    protected List<Float> calculateOffsets(int x, int y, int z, int[][] heightMap, int centerHeight) {
        // No offsets for flat terrain
        return null;
    }
}
