   231→};
   232→
   233→/**
   234→ * Handle previous page
   235→ */
   236→const handlePreviousPage = () => {
   237→  if (!blockTypesComposable.value) return;
   238→  blockTypesComposable.value.previousPage();
   239→};
   240→
   241→onMounted(async () => {
   242→  if (currentWorldId.value && blockTypesComposable.value) {
   243→    await blockTypesComposable.value.loadBlockTypes();
   244→
   245→    // Check if there's a block type ID in the URL
   246→    const params = new URLSearchParams(window.location.search);
   247→    const blockTypeIdParam = params.get('id');
   248→
   249→    if (blockTypeIdParam) {
   250→      const blockTypeId = parseInt(blockTypeIdParam, 10);
   251→      if (!isNaN(blockTypeId)) {
   252→        // Set search query to the ID to show the block type in the list
   253→        searchQuery.value = blockTypeId.toString();
   254→        handleSearch(searchQuery.value);
   255→      }
   256→    }
   257→  }
   258→});
   259→</script>
