   */
  const disableHexGrid = async (q: number, r: number) => {
    loading.value = true;
    error.value = null;

    try {
      await apiClient.post<void>(
        `/control/worlds/${worldId}/hexgrid/${q}/${r}/disable`
      );
      logger.info('Disabled hex grid', { worldId, q, r });
      await loadHexGrids();
    } catch (err) {
