      logger.error('Failed to disable hex grid', { worldId, q, r }, err as Error);
      throw err;
    } finally {
      loading.value = false;
    }
  };

  return {
    hexGrids,
    loading,
    error,
    loadHexGrids,
    loadHexGrid,
    createHexGrid,
    updateHexGrid,
    deleteHexGrid,
    enableHexGrid,
    disableHexGrid,
