import { ref, type Ref } from 'vue';
import { apiService } from '../services/ApiService';
import { getLogger } from '@nimbus/shared';

const logger = getLogger('useJobs');

export type JobStatus = 'PENDING' | 'RUNNING' | 'COMPLETED' | 'FAILED';

export interface Job {
  id: string;
  worldId: string;
  executor: string;
  type: string;
  parameters: Record<string, string>;
  status: JobStatus;
  priority: number;
  maxRetries: number;
  retryCount: number;
  resultData?: string;
  errorMessage?: string;
  createdAt: string;
  startedAt?: string;
  completedAt?: string;
  modifiedAt?: string;
}

export interface JobSummary {
  total: number;
  pending: number;
  running: number;
  completed: number;
  failed: number;
}

export interface JobCreateRequest {
  executor: string;
  type: string;
  parameters: Record<string, string>;
  priority?: number;
  maxRetries?: number;
}

export interface UseJobsReturn {
  jobs: Ref<Job[]>;
  summary: Ref<JobSummary | null>;
  loading: Ref<boolean>;
  error: Ref<string | null>;
  loadJobs: (status?: JobStatus) => Promise<void>;
  loadJob: (jobId: string) => Promise<Job | null>;
  loadSummary: () => Promise<void>;
  createJob: (request: JobCreateRequest) => Promise<void>;
  retryJob: (jobId: string) => Promise<void>;
  cancelJob: (jobId: string) => Promise<void>;
  deleteJob: (jobId: string) => Promise<void>;
}

export function useJobs(worldId: string): UseJobsReturn {
  const jobs = ref<Job[]>([]);
  const summary = ref<JobSummary | null>(null);
